using NUnit.Framework;
using System.Numerics;

namespace Tests
{
    [TestFixture]
    public class Tests
    {
        [SetUp]
        public void Setup()
        {
        }

        [Test]
        public void TestForSmall()
        {
            Assert.AreEqual(new BigInteger(0), Fibonacci.fib(0), "For 0 result should be 0");
            Assert.AreEqual(new BigInteger(1), Fibonacci.fib(1), "For 1 result should be 1");
            Assert.AreEqual(new BigInteger(1), Fibonacci.fib(2), "For 2 result should be 1");
            Assert.AreEqual(new BigInteger(2), Fibonacci.fib(3), "For 3 result should be 2");
            Assert.AreEqual(new BigInteger(3), Fibonacci.fib(4), "For 4 result should be 3");
        }

        [Test]
        public void TestForMid()
        {
            Assert.AreEqual(new BigInteger(2584), Fibonacci.fib(18), "For 18 result should be 2584");
            Assert.AreEqual(new BigInteger(4181), Fibonacci.fib(19), "For 19 result should be 4181");
            Assert.AreEqual(new BigInteger(6765), Fibonacci.fib(20), "For 20 result should be 6765");
        }

        [Test]
        public void TestforBig()
        {
            Assert.AreEqual(BigInteger.Parse("354224848179261915075"), Fibonacci.fib(100), "For 100 result should be 354224848179261915075 ");
            Assert.AreEqual(BigInteger.Parse("43466557686937456435688527675040625802564660517371780402481729089536555417949051890403879840079255169295922593080322634775209689623239873322471161642996440906533187938298969649928516003704476137795166849228875"), 
                                Fibonacci.fib(1000), 
                                "For 1000 result should be ... ");
            Assert.AreEqual(BigInteger.Parse(""), 
                                Fibonacci.fib(10000), 
                                "For 10000 result should be ... ");
            Assert.Greater(Fibonacci.fib(100000),
                                BigInteger.Parse(""), 
                                "For 100000 result should be ... ");
            Assert.Greater(Fibonacci.fib(1000000),
                                BigInteger.Parse(""),
                                "For 1000000 result should be ... ");
        }

        [Test]
        public void TestForBiggest()
        {
            Assert.Greater(Fibonacci.fib(2000000),
                                BigInteger.Parse(""),
                                "For 2000000 result should be ... ");
        }

        [Test]
        public void TestForNeg()
        {
            Assert.AreEqual(new BigInteger(1), Fibonacci.fib(-1), "For -1 result should be 1");
            Assert.AreEqual(new BigInteger(-1), Fibonacci.fib(-2), "For -2 result should be -1");
            Assert.AreEqual(new BigInteger(2), Fibonacci.fib(-3), "For -3 result should be 2");
            Assert.AreEqual(new BigInteger(-3), Fibonacci.fib(-4), "For -4 result should be -3");
        }
    }
}